<script lang="ts">
	let light: SVGPathElement;
	let dark: SVGPathElement;
	const force = {
		y: 0.007,
		x: 0.006
	};
	const limit = 100;

	const calculateForce = (value: number, type: 'x' | 'y') => {
		if (value >= -limit && value <= limit) {
			return (force[type] * value) / limit;
		}

		if (value > limit) {
			return force[type];
		}

		if (value < -limit) {
			return -force[type];
		}

		return 0;
	};

	const update = (x: number, y: number) => {
		let { x: lightX, y: lightY, width: lightW, height: lightH } = light.getBoundingClientRect();
		let { x: darkX, y: darkY, width: darkW, height: darkH } = dark.getBoundingClientRect();

		lightX += lightW / 2;
		lightY += lightH / 2;
		darkX += darkW / 2;
		darkY += darkH / 2;

		const ligthDistance = {
			x: x - lightX,
			y: y - lightY
		};

		const darkDistance = {
			x: x - darkX,
			y: y - darkY
		};

		const lightDepth = {
			x: calculateForce(ligthDistance.x, 'x'),
			y: calculateForce(ligthDistance.y, 'y')
		};

		const darkDepth = {
			x: calculateForce(darkDistance.x, 'x'),
			y: calculateForce(darkDistance.y, 'y')
		};

		light.style.transform = `translate(${lightDepth.x * 100}%, ${lightDepth.y * 100}%)`;
		dark.style.transform = `translate(${darkDepth.x * 100}%, ${darkDepth.y * 100}%)`;
	};
</script>

<svelte:window on:mousemove={(e) => update(e.clientX, e.clientY)} />

